// bie≈ºƒÖca temperatura z msg.payload[0]
let tempCO = Number(msg.payload);

// r√≥≈ºnica temperatur
let delta = tempNastawa - tempCO; 

// --- licznik cykli ---
let cykl = flow.get("cykl") || 0;
cykl++;
if (cykl > 1000) cykl = 0; // reset co 1000
flow.set("cykl", cykl);

// minimalny skok
let minSkok = 100;
let skok = 0;

if (aktywny) {
    // logika skoku: szybkie nagrzewanie i stabilizacja
    if (delta > 8) skok = 1500;
    else if (delta > 5) skok = 1000;
    else if (delta > 2) skok = 500;
    else if (delta > 0.5) skok = 100;
    else skok = 0;

    if (skok < minSkok) skok = minSkok;

    // regulacja si≈Çownika

    stan = "black";

    if (delta > 0.5) { 
        stan = "red"; 
        pozycja = pozycja + skok; 
    } else if (delta < -0.5) { 
        stan = "blue"; 
        pozycja = pozycja - skok; 
    } else { 
        stan = "green"; 
    }
} else {
    // poza harmonogramem ‚Üí OFF
    stan = "grey";
    skok = 0;
    pozycja = 0;
}
// ograniczenie zakresu i warto≈õci ca≈Çkowitej
pozycja = Math.max(minPozycja, Math.min(maxPozycja, Math.round(pozycja)));

flow.set("pozycja", pozycja, "default");

