// === PARAMETRY ===
let tempNastawa = Number(flow.get("temperatura_nastawa")) || 0;
let tempCO = Number(msg.payload);
let aktywny = flow.get("aktywny") ?? true;

let maxPozycja = 5000;
let minPozycja = 0;

// --- Stan poprzedni ---
let pozycja = flow.get("pozycja") || 0;
let cykl = flow.get("cykl") || 0;
cykl++;
if (cykl > 1000) cykl = 0;
flow.set("cykl", cykl);

// --- Bufor r√≥≈ºnicy temperatur ---
let delta = tempNastawa - tempCO;
let buf = flow.get("deltaBuf") || [];
buf.push(delta);
if (buf.length > 5) buf.shift();
let deltaAvg = buf.reduce((a, b) => a + b, 0) / buf.length;
flow.set("deltaBuf", buf);

// === HISTORIA / ADAPTACJA ===
let adapt = flow.get("adapt") || {};
let tKey = Math.round(tempNastawa);

if (!(tKey in adapt)) {
    adapt[tKey] = pozycja;
}

let target = adapt[tKey];

// === UWZGLƒòDNIENIE BEZW≈ÅADNO≈öCI INSTALACJI DYNAMICZNEJ ---
let now = Date.now();
let lastMove = flow.get("lastMove") || 0;
let czasOdZmiany = (now - lastMove) / 1000; // sekundy

// dynamiczny czas bezw≈Çadno≈õci: min 5 min, max 15 min, proporcjonalnie do skoku
let maxBez = 900; // 15 min
let minBez = 300; // 5 min
let skokSzac = Math.min(Math.abs(target - pozycja), maxPozycja);
let bezwladnosc = minBez + skokSzac / maxPozycja * (maxBez - minBez);

let czekaj = czasOdZmiany < bezwladnosc;

// === LOGIKA REGULACJI ===
let skok = 0;
let stan = "grey";
let maxSkok = 300; // maksymalny skok na cykl
let histereza = 10; // minimalny ruch aby zmieniƒá pozycjƒô

if (aktywny) {
    if (!czekaj) {
        if (deltaAvg > 3) skok = 300;
        else if (deltaAvg > 1) skok = 150;
        else if (deltaAvg > 0.5) skok = 50;
        else if (deltaAvg < -3) skok = -300;
        else if (deltaAvg < -1) skok = -150;
        else if (deltaAvg < -0.5) skok = -50;

        skok = Math.max(-maxSkok, Math.min(maxSkok, skok));
        if (Math.abs(skok) < histereza) skok = 0;

        // ‚úÖ ZAMIANA target + skok ‚Üí pozycja + skok
        pozycja = pozycja + skok;

        if (skok !== 0) flow.set("lastMove", now);

        stan = skok > 0 ? "red" : skok < 0 ? "blue" : "green";
    } else {
        // w okresie bezw≈Çadno≈õci ‚Äì pozycja bez zmian
        pozycja = target;
        stan = "yellow";
    }
} else {
    pozycja = 0;
    stan = "grey";
}

// --- ograniczenie ---
pozycja = Math.max(minPozycja, Math.min(maxPozycja, pozycja));

// === WYG≈ÅADZENIE POZYCJI ===
let bufPoz = flow.get("bufPoz") || [];
bufPoz.push(pozycja);
if (bufPoz.length > 5) bufPoz.shift();
let pozycjaFiltrowana = bufPoz.reduce((a, b) => a + b, 0) / bufPoz.length;
flow.set("bufPoz", bufPoz);
pozycja = Math.round(pozycjaFiltrowana / 100) * 100;

// === AKTUALIZACJA HISTORII ===
let adaptFactor = adapt.hasOwnProperty(tKey) ? 0.2 : 0.5; // nowe punkty uczƒÖ siƒô szybciej
adapt[tKey] = Math.round((adapt[tKey] || pozycja) * (1 - adaptFactor) + pozycja * adaptFactor);
flow.set("adapt", adapt);

// --- zapis pozycji ---
flow.set("pozycja", pozycja, "default");

// === STATUS ===
