// Funkcja, która wybiera nastawę na podstawie tempzew
// tutaj prosta interpolacja liniowa między punktami tabeli
function obliczNastawa(tempzew, tabela) {
    for (let i = 0; i < tabela.length - 1; i++) {
        let [t1, n1] = tabela[i];
        let [t2, n2] = tabela[i + 1];
        if (tempzew >= t1 && tempzew <= t2) {
            // interpolacja liniowa
            let nastawa = n1 + (n2 - n1) * (tempzew - t1) / (t2 - t1);
            return Number(nastawa.toFixed(1)); // zaokrąglenie do 1 miejsca
        }
    }
    // jeśli tempzew poza zakresem, zwracamy krańcową wartość
    if (tempzew < tabela[0][0]) return Number(tabela[0][1].toFixed(1));
    if (tempzew > tabela[tabela.length - 1][0]) return Number(tabela[tabela.length - 1][1].toFixed(1));
}

// obliczamy temperaturę nastawy
let tempNastawa = obliczNastawa(tempzew, tabelaNastaw);

