return [out1, out2];

// Pobieramy temperaturę zewnętrzną z flow
let tempzew = Number(flow.get("tempzew")) || 0;

// Tworzymy dwuwymiarową tablicę z przykładowymi wartościami
// format: [ [temp_zew, temp_nastawa], ... ]
let tabelaNastaw = [
    [-20, 55],
    [-10, 50],
    [0, 50],
    [5, 45],
    [10, 40],
    [15, 38],
    [20, 20]
];

// Funkcja, która wybiera nastawę na podstawie tempzew
// tutaj prosta interpolacja liniowa między punktami tabeli
function obliczNastawa(tempzew, tabela) {
    for (let i = 0; i < tabela.length - 1; i++) {
        let [t1, n1] = tabela[i];
        let [t2, n2] = tabela[i + 1];
        if (tempzew >= t1 && tempzew <= t2) {
            // interpolacja liniowa
            let nastawa = n1 + (n2 - n1) * (tempzew - t1) / (t2 - t1);
            return nastawa;
        }
    }
    // jeśli tempzew poza zakresem, zwracamy krańcową wartość
    if (tempzew < tabela[0][0]) return tabela[0][1];
    if (tempzew > tabela[tabela.length - 1][0]) return tabela[tabela.length - 1][1];
}

// obliczamy temperaturę nastawy
let tempNastawa = obliczNastawa(tempzew, tabelaNastaw);

// zapis do flow
flow.set("temperatura_nastawa", tempNastawa);

// dla debugu wyjście msg
msg.payload = { tempzew, tempNastawa, tabelaNastaw };
return msg;

