let tempOut = msg.payload.temp_out;
let tempCO = msg.payload.temp_co;
let valveActual = msg.payload.valve_feedback || 0;

let targetTemp;
let valveCmd;
let pumpState;

if (tempOut >= 12) {
    targetTemp = 0;
    valveCmd = 0;
    pumpState = "OFF";
} else {
    // krzywa grzewcza
    if (tempOut <= 0) targetTemp = 50;
    else if (tempOut <= 5) {
        targetTemp = 50 - (tempOut / 5) * 3;
    } else if (tempOut <= 10) {
        targetTemp = 47 - ((tempOut - 5) / 5) * 5;
    } else {
        targetTemp = 42;
    }

    // regulacja P
    let errorTemp = targetTemp - tempCO;
    if (Math.abs(errorTemp) < 0.5) errorTemp = 0;

    let Kp = 300;
    let deltaCmd = errorTemp * Kp;

    let lastCmd = flow.get("last_valve_cmd") || valveActual;

    let maxDelta = 1000;
    if (deltaCmd > maxDelta) deltaCmd = maxDelta;
    if (deltaCmd < -maxDelta) deltaCmd = -maxDelta;

    valveCmd = Math.max(0, Math.min(10000, lastCmd + deltaCmd));
    pumpState = "ON";
}

flow.set("last_valve_cmd", valveCmd);

let valveError = Math.abs(valveCmd - valveActual);
let warn = "";
if (valveError > 1500 && pumpState === "ON") {
    warn = "⚠ Siłownik nie osiąga pozycji";
}

// ➕ Status w edytorze Node-RED
node.status({
    fill: pumpState === "ON" ? "green" : "grey",
    shape: warn ? "ring" : "dot",
    text: `Tzew: ${tempOut}°C | TCO: ${tempCO}°C → doc: ${targetTemp.toFixed(1)}°C | Sił: ${Math.round(valveCmd)}/${valveActual} | Pompa: ${pumpState}`
});

// Wyjścia
return [
    { payload: Math.round(valveCmd) },
    { payload: pumpState },
    { payload: targetTemp },
    { payload: warn }
];

// Aktualna temperatura
let temp = flow.get("sym_temp") || 20.0;

// Pozycja zaworu (0–10000)
let pos = flow.get("zawor_pozycja") || 0;
let posFraction = Math.min(Math.max(pos / 10000, 0), 1);

// Stan pompy
let pompaOn = flow.get("pompa_stan") === true;

// Parametry
let heatingMaxRatePerMin = 10.0;   // szybki wzrost temp (pompa OFF, zawór otwarty)
let heatingRateWithPump = 0.3;     // wolniejsze grzanie przy pompie ON
let coolingRatePassive = -0.05;    // chłodzenie bez pompy, zawór zamknięty
let coolingRateActive = -10.0;     // chłodzenie z pompą i zaworem zamkniętym

// Krok symulacji
let stepSec = 5;
let stepFrac = stepSec / 60;

let deltaPerStep;

if (pos === 0) {
    // Zawór całkowicie zamknięty → spadek temp
    deltaPerStep = (pompaOn ? coolingRateActive : coolingRatePassive) * stepFrac;
} else if (pos < 2000) {
    // Małe otwarcie zaworu — szybkie chłodzenie jak przy zamkniętym
    deltaPerStep = (pompaOn ? coolingRateActive : coolingRatePassive) * stepFrac;
} else {
    // Zawór bardziej otwarty — interpolacja grzania/chłodzenia
    if (!pompaOn) {
        deltaPerStep = heatingMaxRatePerMin * posFraction * stepFrac;
    } else {
        let coolingBase = coolingRatePassive;
        deltaPerStep = (coolingBase + (heatingRateWithPump - coolingBase) * posFraction) * stepFrac;
    }
}

// Nowa temperatura
let newTemp = temp + deltaPerStep;

// Ograniczenia temperatury
if (newTemp > 70) newTemp = 70;
if (newTemp < 15) newTemp = 15;

flow.set("sym_temp", newTemp);

node.status({
  fill: "blue",
  shape: "ring",
  text: `Temp: ${newTemp.toFixed(2)} °C | ΔT: ${deltaPerStep.toFixed(3)} °C`
});

msg.payload = parseFloat(newTemp.toFixed(2));
return msg;

